import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries} from 'react-vis';
import { Network, Node, Edge } from 'react-vis-network';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const Decorator = props => {
  return (
    <button
      onClick={() => console.log(`You clicked ${props.label}`)}
    >
      Click Me
    </button>
  );
};

const nodes = [
  <Node id="vader" label="Darth Vader" decorator={Decorator}/>,
  <Node id="luke" label="Luke Skywalker" decorator={Decorator}/>,
  <Node id="leia" label="Leia Organa" decorator={Decorator}/>,
  <Node id="han" label="Han Solo" decorator={Decorator}/>,
  <Node id="chewy" label="Chewbacca" decorator={Decorator}/>,
  <Node id="obi" label="Obi-Wan Kenobi" decorator={Decorator}/>
];

const edges = [
  <Edge id="1" from="vader" to="luke" />,
  <Edge id="2" from="vader" to="leia" />,
  <Edge id="3" from="han" to="leia" />,
  <Edge id="4" from="han" to="chewy" />,
  <Edge id="5" from="obi" to="luke" />,
  <Edge id="6" from="leia" to="luke" />,
  <Edge id="7" from="vader" to="obi" />
];

class NoteWeb extends React.Component {
  // This is the main container for all content
  constructor(props) {
    super(props);
    this.top = <TopBar />;
    this.main = <Main />;
  }

  render() {
    return (
      <div className="noteweb">
        {this.top,
        this.main}
      </div>
    );
  }
}

class TopBar extends React.Component {
  // This is the header of the application
  render() {
    return (
      <div>
        <div className="container">
          <div className="topbar">
            <h1>Noteweb v0.0</h1>
          </div>
          <div className="account">
          </div>
        </div>
      </div>
    );
  }
}

class Main extends React.Component {
  // This is the main content of the application, and holds the left side bar and the notes display
  render() {
    return (
      <div className="container">
        <LeftBar />
        <div className="main-content">
          <DataScreen />
          <NoteScreen />
        </div>
      </div>
    );
  }
}

class LeftBar extends React.Component {
  // Content on left side of screen
  render() {
    return (
      <div className="leftbar">
        <p>Some leftbar stuff</p>
      </div>
    );
  }
}

class DataScreen extends React.Component {
  //The notes graph display
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.items = nodes.concat(edges);
    this.network = <div className="datascreen"><Network>{this.items}</Network></div>;
  } 

  setNetwork() {
    this.network = <div className="datascreen"><Network></Network></div>;
  }

  render() {
    return (
      this.network
    );
  }
}

class NoteScreen extends React.Component {
  // the note content display
  render() {
    return(
      <div className="notescreen">
        {/* <p>Some main datascreen stuff</p> */}
        <container width="100%" height="100%">
          <h3> Note Title </h3>
          <p> note content </p>
        </container>
      </div>
    );
  }

}

// ========================================

ReactDOM.render(
  <NoteWeb />,
  document.getElementById('root')
);
