import React from 'react';
import ReactDOM from 'react-dom';
import { Graph } from 'vis-react';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';




var nodes = [
  { id: 1, label: 'Node 1' },
  { id: 2, label: 'Node 2' },
  { id: 3, label: 'Node 3' },
  { id: 4, label: 'Node 4' },
  { id: 5, label: 'Node 5' }
];

var edges: [
  { from: 1, to: 2 }, 
  { from: 1, to: 3 }, 
  { from: 2, to: 4 }, 
  { from: 2, to: 5 }
];

var options = {
    layout: {
        hierarchical: true
    },
    edges: {
        color: '#000000'
    }
};

var events = {
    select: function(event) {
        var { nodes, edges } = event;
    }
};

var graph = {
  nodes,
  edges
};

class NoteWeb extends React.Component {
  // This is the main container for all content
  render() {
    return (
      <div className="noteweb">
        <TopBar />
        <Main />
      </div>
    );
  }
}

class TopBar extends React.Component {
  // This is the header of the application
  render() {
    return (
      <div>
        <div className="container">
          <div className="topbar">
            <h1>Noteweb v0.0</h1>
          </div>
          <div className="account">
          </div>
        </div>
      </div>
    );
  }
}

class Main extends React.Component {
  // This is the main content of the application, and holds the left side bar and the notes display
  render() {
    return (
      <div className="container">
        <LeftBar />
        <div className="main-content">
          <DataScreen />
          <NoteScreen />
        </div>
      </div>
    );
  }
}

class LeftBar extends React.Component {
  // Content on left side of screen
  render() {
    return (
      <div className="leftbar">
        <p>Some leftbar stuff</p>
      </div>
    );
  }
}

class DataScreen extends React.Component {
  //The notes graph display
  attachZoom() {
    console.log("hi");
  }

  render() {
    return (
      <div className="datascreen">
        {/* <p>Some main datascreen stuff</p> */}
        <svg width="100%" height="100%">
          // <rect width={500} height={500} rx={14} fill="#272b4d" />
          <Graph graph={graph}
            options={options}
            events={events}
            // style={style}
            getNetwork={this.getNetwork}
            getEdges={this.getEdges}
            getNodes={this.getNodes}
            vis={vis => (this.vis = vis)} />
        </svg>
      </div>
    );
  }
}

class NoteScreen extends React.Component {
  // the note content display
  render() {
    return(
      <div className="notescreen">
        {/* <p>Some main datascreen stuff</p> */}
        <container width="100%" height="100%">
          <h3> Note Title </h3>
          <p> note content </p>
        </container>
      </div>
    );
  }

}

// ========================================

ReactDOM.render(
  <NoteWeb />,
  document.getElementById('root')
);
