import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries} from 'react-vis';
import { Network, Node, Edge } from 'react-vis-network';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const nodes = [
  <Node id="vader" label="Darth Vader" />,
  <Node id="luke" label="Luke Skywalker" />,
  <Node id="leia" label="Leia Organa" />
];

const edges = [
  <Edge id="1" from="vader" to="luke" />,
  <Edge id="2" from="vader" to="leia" />
];

class NoteWeb extends React.Component {
  // This is the main container for all content
  render() {
    return (
      <div className="noteweb">
        <TopBar />
        <Main />
      </div>
    );
  }
}

class TopBar extends React.Component {
  // This is the header of the application
  render() {
    return (
      <div>
        <div className="container">
          <div className="topbar">
            <h1>Noteweb v0.0</h1>
          </div>
          <div className="account">
          </div>
        </div>
      </div>
    );
  }
}

class Main extends React.Component {
  // This is the main content of the application, and holds the left side bar and the notes display
  render() {
    return (
      <div className="container">
        <LeftBar />
        <div className="main-content">
          <DataScreen />
          <NoteScreen />
        </div>
      </div>
    );
  }
}

class LeftBar extends React.Component {
  // Content on left side of screen
  render() {
    return (
      <div className="leftbar">
        <p>Some leftbar stuff</p>
      </div>
    );
  }
}

class DataScreen extends React.Component {
  //The notes graph display
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.items = nodes.concat(edges);
    this.network = <div class="datascreen"><Network>{this.items}</Network></div>;
  } 

  setNetwork() {
    this.network = <div class="datascreen"><Network></Network></div>;
  }

  render() {
    return (
      this.network
    );
  }
}

class NoteScreen extends React.Component {
  // the note content display
  render() {
    return(
      <div className="notescreen">
        {/* <p>Some main datascreen stuff</p> */}
        <container width="100%" height="100%">
          <h3> Note Title </h3>
          <p> note content </p>
        </container>
      </div>
    );
  }

}

// ========================================

ReactDOM.render(
  <NoteWeb />,
  document.getElementById('root')
);
