import React, { Component } from 'react';
import '../node_modules/react-vis/dist/style.css';
import { InteractiveForceGraph, ForceGraph, ForceGraphNode, ForceGraphLink } from 'react-vis-force';
import Graph from 'react-graph-vis'
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class DataScreen extends Component {
  /**
   * This screen displays the network of notes and allows selection of a note to be displayed in the NoteScreen
   */
  constructor(props) {
    super(props);
    console.log(props);
    this.state = {graph:this.props.state.graph, getnote:this.props.getNote};

    this.options = {
      layout: {
        hierarchical: false
      },
      edges: {
        color: "#FFFFFF"
      },
      height: "500px"
    };

    this.events = {
      select: (event) => {
        var { nodes, edges } = event;
        this.props.getNote(event.nodes[0]);
      }
    };
    // Don't call this.setState() here!
    this.state = {items:this.props.state.items, graph:this.props.state.graph};
    // this.network= <Graph
    //   graph={this.state.graph}
    //   options={this.options}
    //   events={this.events}
    // />;
    this.network = <InteractiveForceGraph
      simulationOptions={{ height: 300, width: 300 }}
      labelAttr="label"
      onSelectNode={(event,node) => console.log(event,node)}
      highlightDependencies
    >
      <ForceGraphNode node={{ id: 'first-node', label: 'First node' }} fill="red" />
      <ForceGraphNode node={{ id: 'second-node', label: 'Second node' }} fill="blue" />
      <ForceGraphLink link={{ source: 'first-node', target: 'second-node' }} />
    </InteractiveForceGraph>
    // console.log(this.network.props.graph.nodes);
  } 

  // componentWillReceiveProps(nextProps) {
  //   /*
  //   * Might help propogate state change down to child from parent
  //   */
  //   console.log("1")
  //   console.log(nextProps);
  //   console.log("2")
  //   console.log(nextProps.state.items);
  //   this.setState({ items: nextProps.state.items});
  //   console.log("3")
  //   console.log(this.state);
  //   console.log(this.network);
  // }



  render() {
    /**
     * Renders the component
     */
    return (
      <div className="datascreen">
        <div>
          <p>HELLO</p>
        </div>
        {this.network}
      </div>
    );
  }
}
