import React from 'react';
import ReactDOM from 'react-dom';
import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries} from 'react-vis';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';




const nodes = [
  { x: 50, y: 20 },
  { x: 200, y: 300 },
  { x: 300, y: 40 }
];

const links = [
  { source: nodes[0], target: nodes[1] },
  { source: nodes[1], target: nodes[2] },
  { source: nodes[2], target: nodes[0] }
];

const graph = {
  nodes,
  links
};

class NoteWeb extends React.Component {
  // This is the main container for all content
  render() {
    return (
      <div className="noteweb">
        <TopBar />
        <Main />
      </div>
    );
  }
}

class TopBar extends React.Component {
  // This is the header of the application
  render() {
    return (
      <div>
        <div className="container">
          <div className="topbar">
            <h1>Noteweb v0.0</h1>
          </div>
          <div className="account">
          </div>
        </div>
      </div>
    );
  }
}

class Main extends React.Component {
  // This is the main content of the application, and holds the left side bar and the notes display
  render() {
    return (
      <div className="container">
        <LeftBar />
        <div className="main-content">
          <DataScreen />
          <NoteScreen />
        </div>
      </div>
    );
  }
}

class LeftBar extends React.Component {
  // Content on left side of screen
  render() {
    return (
      <div className="leftbar">
        <p>Some leftbar stuff</p>
      </div>
    );
  }
}

class DataScreen extends React.Component {
  //The notes graph display
  attachZoom() {
    console.log("hi");
  }

  render() {
    return (
      <div className="App">
        <XYPlot height={300} width={300}>
          <LineSeries data={data} />
        </XYPlot>
      </div>
    );
  }
}

class NoteScreen extends React.Component {
  // the note content display
  render() {
    return(
      <div className="notescreen">
        {/* <p>Some main datascreen stuff</p> */}
        <container width="100%" height="100%">
          <h3> Note Title </h3>
          <p> note content </p>
        </container>
      </div>
    );
  }

}

